INSERT INTO flowers (flower_type_id, name_original, name, color_id, image, flower_size_min, flower_size_max, flower_height_min, flower_height_max, description, group_name) VALUES ((SELECT currval(pg_get_serial_sequence('flower_types','id'))), 'Sorbonne', 'Сорбонне', 6, 'https://www.royalflowersgroup.com/wp-content/uploads/2016/09/Sorbonne-website-1_500x333.jpg', 15, 20, 110, 120, 'Лілія Sorbonne (Сорбонна) орієнтальна - прекрасні рожеві суцвіття виду Oriental hybrids. У цій групі зібрані Східні гібриди, які відрізняються високою толерантністю до умов вирощування, надійними міцними квітконосами, сильним ароматом. При правильній агротехніці з однієї цибулини виростає цілий букет величезних рожевих квіток з малиновим крапом по пелюстках і зеленою підставою. Тонка біла смуга обрамляє кожну пелюстку і стає ширшою на кінчик. Краще орієнтальні лілії ростуть на добре освітлених ділянках, але комфортно почувають себе і в частковій тіні, це дозволяє вирощувати їх в різних умовах; будь-які садові грунти підійдуть для посадки цибулин, але вони повинні бути вологоємні; зимує в саду без укриття до -15 ° С. На випадок безсніжної зими - замульчируйте посадки цибулин лілій торфом або листям шаром до 7 см - 10 см; на одному місці цибулини можуть рости без викопування до 10 років.', 'Орієнтальні Лілії');
INSERT INTO flowers (flower_type_id, name_original, name, color_id, image, flower_size_min, flower_size_max, flower_height_min, flower_height_max, description, group_name) VALUES ((SELECT currval(pg_get_serial_sequence('flower_types','id'))), 'Saltarello', 'Сальтарелло', 3, 'https://yaskravaklumba.com.ua/uploads/shop/products/main/8e4eec5a852bfde78ac09165bb87e742.png', 20, 25, 100, 160, 'Яскраво-помаранчева, помітна в саду, гігантська лілія Сальтарелло порадує Вас і ваших гостей не тільки яскравим кольором, але і приємним ароматом. Лілії гіганти є результатом схрещування орієнтальних і трубчастих лілій, які відрізняються високим зростом і великими квітами. Характерною особливістю є великий розмір квітів. Їх вирощують для прикраси саду і для зрізання, квіти довго стоять у воді, можуть, не втративши зовнішніх даних, протриматися до 2 тижнів у вазі.', 'Лілії Гіганти');
